// Email Data Processor
const input = $input.first().json;
const { topic, recipient, urgency, context } = input;

// Build email prompt based on context
let emailPrompt = `Generate a professional email about: ${topic}`;

if (context) {
  emailPrompt += `\nContext: ${context}`;
}

if (urgency) {
  emailPrompt += `\nUrgency: ${urgency}`;
}

// Add fitness maintenance specific context
emailPrompt += `\n\nThis is for Lifetime Fitness maintenance team. Keep it professional, clear, and actionable.`;

return {
  emailPrompt,
  recipient: recipient || 'team@lifetimefitness.com',
  subject: `Maintenance Update: ${topic}`,
  urgency,
  originalData: input
};

// Email Response Processor
const input = $input.first().json;
const perplexityResponse = input.data;
const emailData = input.originalData;

// Extract email content from Perplexity response
let emailContent = '';
if (perplexityResponse && perplexityResponse.choices && perplexityResponse.choices[0]) {
  emailContent = perplexityResponse.choices[0].message.content;
} else {
  emailContent = 'Unable to generate email content at this time.';
}

// Format email
const formattedEmail = `\n${emailContent}\n\n---\nLifetime Fitness Maintenance Team\nGenerated by AI Assistant`;

return {
  to: emailData.recipient,
  subject: emailData.subject,
  content: formattedEmail,
  urgency: emailData.urgency,
  generatedAt: new Date().toISOString()
};

// AI Assistant Context Builder
const input = $input.first().json;
const { message, context, history } = input;

// Build context for fitness maintenance
let systemPrompt = `You are an AI assistant for Lifetime Fitness maintenance teams. You help with:\n- Equipment maintenance schedules\n- Facility maintenance tasks\n- Safety compliance\n- Supply management\n- Team coordination\n\nProvide practical, actionable advice for fitness facility maintenance.`;

// Add conversation history if available
let messages = [
  { role: 'system', content: systemPrompt }
];

if (history && history.length > 0) {
  messages = messages.concat(history);
}

messages.push({ role: 'user', content: message });

return {
  messages,
  context: context || 'general',
  originalMessage: message
};

// AI Response Processor
const input = $input.first().json;
const aiResponse = input.data;
const context = input.context;

// Extract AI response
let response = '';
if (aiResponse && aiResponse.choices && aiResponse.choices[0]) {
  response = aiResponse.choices[0].message.content;
} else {
  response = 'I apologize, but I am unable to process your request at this time.';
}

// Add context-specific formatting
if (context === 'voice-command') {
  response = `ðŸŽ¤ Voice Command Response:\n\n${response}`;
}

return {
  response,
  context,
  timestamp: new Date().toISOString(),
  originalMessage: input.originalMessage
};

// Task Analysis Processor
const input = $input.first().json;
const { description, priority, equipment, source } = input;

// Build analysis prompt
const analysisPrompt = `Analyze this maintenance task for a fitness facility:

Task: ${description}
Equipment: ${equipment || 'Not specified'}
Priority: ${priority || 'medium'}

Please provide:
1. Task category (equipment, facility, safety, cleaning)
2. Estimated duration
3. Required tools/supplies
4. Safety considerations
5. Priority level (low/medium/high/urgent)
6. Recommended schedule

Format as JSON.`;

return {
  analysisPrompt,
  originalTask: description,
  priority: priority || 'medium',
  equipment: equipment || 'general',
  source: source || 'manual'
};

// Task Result Processor
const input = $input.first().json;
const aiResponse = input.data;
const taskData = input.originalTask;

// Extract AI analysis
let analysis = {};
try {
  if (aiResponse && aiResponse.choices && aiResponse.choices[0]) {
    const content = aiResponse.choices[0].message.content;
    // Try to parse JSON from response
    const jsonMatch = content.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      analysis = JSON.parse(jsonMatch[0]);
    } else {
      analysis = { rawResponse: content };
    }
  }
} catch (error) {
  analysis = { error: 'Failed to parse AI response', rawResponse: aiResponse };
}

// Create task object
const task = {
  id: Date.now().toString(),
  description: taskData,
  category: analysis.category || 'general',
  priority: analysis.priority || 'medium',
  duration: analysis.estimatedDuration || '1 hour',
  tools: analysis.requiredTools || [],
  safety: analysis.safetyConsiderations || [],
  schedule: analysis.recommendedSchedule || 'as needed',
  createdAt: new Date().toISOString(),
  status: 'pending'
};

return {
  task,
  analysis,
  success: true
};

// Photo Analysis Processor
const input = $input.first().json;
const { description, category, imageUrl, imageData } = input;

// Build analysis prompt
let analysisPrompt = `Analyze this maintenance-related photo:\n\nDescription: ${description}\nCategory: ${category || 'equipment-maintenance'}\n\nPlease provide:\n1. Equipment/facility identification\n2. Issue assessment\n3. Severity level (low/medium/high/critical)\n4. Recommended actions\n5. Parts/supplies needed\n6. Safety concerns\n\nFormat as JSON.`;

// If image data is provided, add to prompt
if (imageUrl || imageData) {
  analysisPrompt += `\n\nImage data available for analysis.`;
}

return {
  analysisPrompt,
  description,
  category: category || 'equipment-maintenance',
  imageUrl,
  imageData
};

// Photo Result Processor
const input = $input.first().json;
const aiResponse = input.data;
const photoData = input;

// Extract AI analysis
let analysis = {};
try {
  if (aiResponse && aiResponse.choices && aiResponse.choices[0]) {
    const content = aiResponse.choices[0].message.content;
    // Try to parse JSON from response
    const jsonMatch = content.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      analysis = JSON.parse(jsonMatch[0]);
    } else {
      analysis = { rawResponse: content };
    }
  }
} catch (error) {
  analysis = { error: 'Failed to parse AI response', rawResponse: aiResponse };
}

// Create photo analysis object
const photoAnalysis = {
  id: Date.now().toString(),
  description: photoData.description,
  category: photoData.category,
  equipment: analysis.equipmentIdentification || 'Unknown',
  issue: analysis.issueAssessment || 'No specific issue identified',
  severity: analysis.severityLevel || 'low',
  actions: analysis.recommendedActions || [],
  parts: analysis.partsSuppliesNeeded || [],
  safety: analysis.safetyConcerns || [],
  analyzedAt: new Date().toISOString()
};

return {
  photoAnalysis,
  analysis,
  success: true
};

// Shopping Item Processor
const input = $input.first().json;
const { item, quantity, urgency, budget } = input;

// Build analysis prompt
const analysisPrompt = `Analyze this maintenance shopping item for a fitness facility:

Item: ${item}
Quantity: ${quantity || 1}
Urgency: ${urgency || 'medium'}
Budget: ${budget || 'not specified'}

Please provide:
1. Item category (equipment parts, cleaning supplies, tools, safety equipment)
2. Recommended suppliers (with URLs if possible)
3. Estimated cost range
4. Alternative options
5. Priority level (low/medium/high/urgent)
6. Delivery time estimate
7. Quality recommendations

Format as JSON.`;

return {
  analysisPrompt,
  originalItem: item,
  quantity: quantity || 1,
  urgency: urgency || 'medium',
  budget: budget || 'flexible'
};

// Shopping Result Processor
const input = $input.first().json;
const aiResponse = input.data;
const shoppingData = input;

// Extract AI analysis
let analysis = {};
try {
  if (aiResponse && aiResponse.choices && aiResponse.choices[0]) {
    const content = aiResponse.choices[0].message.content;
    // Try to parse JSON from response
    const jsonMatch = content.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      analysis = JSON.parse(jsonMatch[0]);
    } else {
      analysis = { rawResponse: content };
    }
  }
} catch (error) {
  analysis = { error: 'Failed to parse AI response', rawResponse: aiResponse };
}

// Create shopping item object
const shoppingItem = {
  id: Date.now().toString(),
  item: shoppingData.originalItem,
  quantity: shoppingData.quantity,
  category: analysis.itemCategory || 'general',
  suppliers: analysis.recommendedSuppliers || [],
  costRange: analysis.estimatedCostRange || 'Unknown',
  alternatives: analysis.alternativeOptions || [],
  priority: analysis.priorityLevel || shoppingData.urgency,
  deliveryTime: analysis.deliveryTimeEstimate || 'Unknown',
  quality: analysis.qualityRecommendations || 'Standard',
  addedAt: new Date().toISOString(),
  status: 'pending'
};

return {
  shoppingItem,
  analysis,
  success: true
};

